// SearchComponent.tsx

import React, { useState } from 'react';
import { Button, Form, Alert } from 'react-bootstrap';

interface SearchComponentProps {
  onSearch: (searchValue: string) => void;
  searchValue: string; // Add this line
  setSearchValue: React.Dispatch<React.SetStateAction<string>>; // Add this line
}

const SearchComponent: React.FC<SearchComponentProps> = ({ onSearch, searchValue, setSearchValue }) => {
  const [validationError, setValidationError] = useState('');

  const handleSearch = () => {
    if (!searchValue.trim()) {
      setValidationError('Please enter a search term.');
    } else {
      setValidationError('');
      onSearch(searchValue);
    }
  };

  return (
    <div id='searchBar'>
      <Form className="d-flex">
        <Form.Control
          type="search"
          placeholder="Search"
          className="me-2"
          aria-label="Search"
          value={searchValue} // Controlled input value
          onChange={(event) => {
            setSearchValue(event.target.value);
            setValidationError('');
          }}
        />
        <Button variant="dark" onClick={handleSearch}>
          Search
        </Button>
      </Form>
      {validationError && <Alert variant="danger">{validationError}</Alert>}
    </div>
  );
};

export default SearchComponent;
